import React from "react";
import axios from "axios";

async function addUser(MCQ) {
  return await axios({
    method: "get", //you can set what request you want to be
    url: process.env.REACT_APP_BASE_URL + "/api/user",
  })
    .then((users) => {
      users.data.map(
        (user) =>
          user.email === userInfo.email &&
          user.password === userInfo.password &&
          (find = 1)
      );
      return find !== 0 ? true : false;
    })
    .catch((error) => infoMessage(error.message, false));
}

function getUser() {}

async function addMCQ(MCQ) {
  return await axios({
    method: "post",
    url: process.env.REACT_APP_BASE_URL + "/api/quiz",
  })
    .then((users) => {
      users.data.map(
        (user) =>
          user.email === userInfo.email &&
          user.password === userInfo.password &&
          (find = 1)
      );
      return find !== 0 ? true : false;
    })
    .catch((error) =>  error.message);
}

async function getMCQ() {
  return await axios({
    method: "get",
    url: process.env.REACT_APP_BASE_URL + "/api/quiz",
  })
    .then((users) => {
      return users.data;
    })
    .catch((error) => {
      return error.message;
    });
}

export default { getMCQ, addMCQ };
