import React from "react";
import { useSelector } from "react-redux";
import { CircularProgressbar } from 'react-circular-progressbar';


function ScoresShow({ MCQs }) {
  const results = useSelector((state) => state.scoresTrack.scores);
  function calculateScores() {
    var scores = 0;
    MCQs &&
      results.forEach((result, index) => {
        MCQs[index].answer === result && (scores += 1);
      });
    return scores;
  }

  return (
    <div className="content w-100 ">
      <div className="Quizheader text-center mb-2">
        {/* {"You have Scored "}
        <br />
        {calculateScores()}
        <br /> {" out of " + MCQs.length} */}
        <CircularProgressbar value={calculateScores()/MCQs.length * 100} text={`${ calculateScores()} out of ${MCQs.length}` } />
      </div>
    </div>
  );
}

export default ScoresShow;
