import React, { useState } from "react";
import { Button, ButtonGroup, ToggleButton } from "react-bootstrap";
function Question() {
  const [checked, setChecked] = useState(1);
  const radios = [
    { name: "Active", value: "1" },
    { name: "Radio", value: "2" },
    { name: "Radio", value: "3" },
  ];
  return (
    <div>
      <ButtonGroup>
        {radios.map((radio, idx) => (
         <Button
          className="mb-2"
          id="toggle-check"
          type="checkbox"
          variant={checked === radio.idx ? " outline-primary" : "primary"}
          checked={checked}
          name={radio.name}
          value={""+radio.idx}
          onChange={(e) => setChecked(e.currentTarget.checked)}
        >
          Checked
        </Button>
        ))}
      </ButtonGroup>
      <div className="QuizQuestion">Who was quaid-e-azam?</div>
      <div className="QuizContent">
        <div className="centered">
          <Button className="quizbtn">Start Quiz</Button>
        </div>
      </div>
    </div>
  );
}

export default Question;
