import React from "react";
import axios from "axios";

async function addUser(MCQ) {
  return await axios({
    method: "get", //you can set what request you want to be
    url: process.env.REACT_APP_BASE_URL + "/api/user",
  })
    .then((users) => {
      users.data.map((users) => console.log(users));
    })
    .catch((error) => error.message);
}

async function getMCQbyid(id) {
  return await axios({
    method: "get", //you can set what request you want to be
    url: process.env.REACT_APP_BASE_URL + "/api/quiz/" + id,
  });
}

async function addMCQ(MCQ) {
  return await axios({
    method: "post", //you can set what request you want to be
    url: process.env.REACT_APP_BASE_URL + "/api/quiz",
    data: MCQ,
    headers: {
      "Content-Type": "application/json",
    },
  });
}
async function updateMCQ(id, MCQ) {
  return await axios({
    method: "patch", //you can set what request you want to be
    url: process.env.REACT_APP_BASE_URL + "/api/quiz/"+id,
    data: MCQ,
    headers: {
      "Content-Type": "application/json",
    },
  });
}

async function getMCQ() {
  return await axios({
    method: "get",
    url: process.env.REACT_APP_BASE_URL + "/api/quiz",
  })
    .then((users) => {
      return users.data;
    })
    .catch((error) => {
      return error.message;
    });
}

export default { getMCQ, addMCQ, getMCQbyid, updateMCQ };
