import React, { useState } from "react";
import { Button, ButtonGroup, ToggleButton } from "react-bootstrap";
import Optionbtn from "../btnOption";
function Question({MCQ, index}) {
  const { mcqCount } = useSelector(state => state.mcqCounter .mcqCount)

  const options = ["A", "B", "C", "D", "E", "F", "G","H"];
  const [checked, setChecked] = useState("None");
  return (
    <div className="content w-100 ">
      <div className="Quizheader mb-2">{"Question # "+index+":"}</div>
      <div className="QuizQuestion m-2">{MCQ.question}</div>
      <div className="QuizContent">
        {MCQ.options.map((option,index) => (
          <Optionbtn text= {options[index]+". "+option} id={options[index]} onClick={() => setChecked(options[index]) } checked={checked !== options[index] } />
        ))}

        <div className="centered">
          <Button className="quizbtn w-25">Previous</Button>
          <Button className="quizbtn w-25">{"Next"}</Button>
        </div>
      </div>
    </div>
  );
}

export default Question;
