import React, { useEffect, useState } from "react";
import { Modal, Button, Form, Row, Col } from "react-bootstrap";
import {getMCQ} from "../db/index";

function QuizModal() {
  const [show, setShow] = useState(true);
  const [optionNum, setOptionNum] = useState(2);
  const alpha = ["A.", "B.", "C.", "D.", "E.", "F.", "G.", "H."];
  function handleClose() {
    setShow(false);
  }

  useEffect(() => {
    getMCQ().then((mcqs) => console.log(mcqs));
  }, []);

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header>
        <Modal.Title className="normalFont">Add M.C.Q</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Question</Form.Label>
            <Form.Control type="question" placeholder="Enter question" />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Options</Form.Label>
            {alpha.map(
              (options, index) =>
                index < optionNum && (
                  <Row className="mt-2">
                    <Col>
                      <div className="mt-1 text-center normalFont">
                        {options}
                      </div>
                    </Col>
                    <Col xs={10}>
                      <Form.Control
                        type="options"
                        placeholder="Option"
                        required
                      />
                    </Col>
                  </Row>
                )
            )}
          </Form.Group>
        </Form>
        <div className="d-flex ">
          <Button
            disabled={optionNum > alpha.length - 1}
            variant="outline-success "
            onClick={() => setOptionNum(optionNum + 1)}
            block
            className="addOptionBtn "
          >
            <ion-icon name="add-circle-outline"></ion-icon>
          </Button>
          <Button
            disabled={optionNum < 3}
            variant="outline-danger "
            onClick={() => setOptionNum(optionNum - 1)}
            className="addOptionBtn "
          >
            <ion-icon name="close-circle-outline"></ion-icon>
          </Button>
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleClose}>
          Save Changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
}

export default QuizModal;
