  async function getScores(){
    return await axios({
      method: "get", //you can set what request you want to be
      url: process.env.REACT_APP_BASE_URL + "/api/user",
    }).then((users) => {
      users.data.map(
        (user) =>
          user.email === userInfo.email &&
          user.password === userInfo.password &&
          (find = 1)
      );
      return find !== 0 ? true : false;
    }).catch(error =>  infoMessage(error.message, false));
  }

  return (
    <div>
      <Navigationbar />

      <div className="sm-table">
        <div className="Tablehead">Quiz Table</div>
        <div className="addbtn">
          <Button variant="outline-secondary" className="btnTable ">
            <ion-icon name="add-circle-outline"></ion-icon>
          </Button>
        </div>
        <Table className="responsive " bordered>
          <thead>
            <tr className="text-center">
              <th>#</th>
              <th>Questions</th>
              <th></th>
            </tr>
          </thead>
          <tbody className="normalFont text-center">
            <tr>
              <td>1</td>
              <td>What is your name?</td>
              <td className="w-25">
                <Button variant="outline-secondary" className="btnTable">
