                  <ion-icon name="create-outline"></ion-icon>
                </Button>{" "}
                <Button variant="outline-secondary" className="btnTable">
                  <ion-icon name="close-circle-outline"></ion-icon>
                </Button>{" "}
              </td>
            </tr>
          </tbody>
        </Table>
      </div>
    </div>
  );
}

export default QuizManage;
