import React from "react";
import "./App.css";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";
import Login from "./Components/Pages/login";
import Signup from "./Components/Pages/signup";
import Home from "./Components/Pages/home";
import QuizManage from "./Components/Pages/QuizManagement";
import Scores from "./Components/Pages/Scores";
import { ReactSession } from "react-client-session";

function App() {
  // const [data, setData] = React.useState(null);
  ReactSession.setStoreType("cookie");
  ReactSession.set("id", res.data._id);


  const PrivateRoute = ({ component: Component, ...rest }) => (
    <Route
      {...rest}
      render={(props) =>
         ReactSession.get("id") !== undefined ? (
          <Component {...props} />
        ) : (
          <Redirect to={{ pathname: "/login" }} />
        )
      }
    />
  );

  return (
    <Router>
      <Switch>
        <Route exact path="/login">
          <Login />
        </Route>
        {console.log(ReactSession.get("id"))}
        <Route path="/signup">
          <Signup />
        </Route>
        <PrivateRoute exact path="/" component={Home} exact />
        <PrivateRoute exact path="/QuizManage" component={QuizManage} />
        <PrivateRoute  exact path="/Scores" component={Scores} />
      </Switch>
    </Router>
  );
}

export default App;
