import React, { useState } from "react";
import { Button, ButtonGroup, ToggleButton } from "react-bootstrap";
function Question() {
  const [checked, setChecked] = useState("1");
  const radios = [
    { name: "Active", value: "1" },
    { name: "Radio", value: "2" },
    { name: "Radio", value: "3" },
  ];
  return (
    <div>
        {console.log(checked)}
      <ButtonGroup>
        {radios.map((radio, idx) => (
         <Button
          className="mb-2"
          id="toggle-check"
          type="checkbox"
          variant={" outline-primary" }
          checked={checked == Int16Array(radio.value) ? true : false}
          name={radio.name}
          value={radio.value}
          onClick={(e) => setChecked(radio.value)}
        >
          {radio.name}
        </Button>
        ))}
      </ButtonGroup>
      <div className="QuizQuestion">Who was quaid-e-azam?</div>
      <div className="QuizContent">
        <div className="centered">
          <Button className="quizbtn">Start Quiz</Button>
        </div>
      </div>
    </div>
  );
}

export default Question;
