import React, { useEffect, useState } from "react";
import { Modal, Button, Form, Row, Col } from "react-bootstrap";
import db from "../db/index";

function QuizModal({ visibility, setVisiblity, id, task }) {
  const [optionNum, setOptionNum] = useState(2);

  const [quiz, setquiz] = useState({});
  const alpha = ["A.", "B.", "C.", "D.", "E.", "F.", "G.", "H."];

  useEffect(() => {
    if(task != "add"){
      db.getMCQbyid().then((mcqs) => console.log(mcqs));
    }

  }, []);

  function submit() {
    db.addMCQ(quiz)
      .then((mcqs) => console.log(mcqs))
      .catch((e) => console.log(e));
  }

  function handleChange(e) {
    setquiz({ ...quiz, [e.target.name]: e.target.value });
  }

  function handleOptions(e) {
    let options = quiz.options !== undefined ? quiz.options : [];
    options[e.target.name] = e.target.value;
    setquiz({ ...quiz, [e.target.placeholder]: [...options] });
  }

  function handleRemove() {
    let options = quiz.options;
    options.pop();
    setquiz({ ...quiz, options: [...options] });
  }

  return (
    <Modal show={visibility}>
      {/* Header */}
      <Modal.Header>
        <Modal.Title className="normalFont">Add M.C.Q</Modal.Title>
      </Modal.Header>
      {/* Body */}

      <Form onSubmit={submit}>
        <Modal.Body>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Question</Form.Label>
            <Form.Control
              required
              name="question"
              type="question"
              placeholder="Enter question"
              onChange={handleChange}
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicPassword">
            {/* Option */}
            <Form.Label>Options</Form.Label>
            {alpha.map(
              (options, index) =>
                index < optionNum && (
                  <Row className="mt-2">
                    <Col>
                      <div className="mt-1 text-center normalFont">
                        {options}
                      </div>
                    </Col>
                    <Col xs={10}>
                      <Form.Control
                        name={index}
                        placeholder="options"
                        required
                        onChange={handleOptions}
                      />
                    </Col>
                  </Row>
                )
            )}
            {/* Add and Remove btn */}
          </Form.Group>
          <div className="d-flex ">
            <Button
              disabled={optionNum > alpha.length - 1}
              variant="outline-success "
              onClick={() => setOptionNum(optionNum + 1)}
              block
              className="addOptionBtn "
            >
              <ion-icon name="add-circle-outline"></ion-icon>
            </Button>
            <Button
              disabled={optionNum < 3}
              variant="outline-danger "
              onClick={() => {
                setOptionNum(optionNum - 1);
                handleRemove();
              }}
              className="addOptionBtn "
            >
              <ion-icon name="close-circle-outline"></ion-icon>
            </Button>
          </div>
        </Modal.Body>
        {/* Footer */}
        <Modal.Footer>
          <Button variant="secondary" onClick={setVisiblity}>
            Close
          </Button>
          <Button variant="primary" type="submit">
            Save Changes
          </Button>
        </Modal.Footer>
      </Form>
    </Modal>
  );
}

export default QuizModal;
